import React, { useEffect, useState } from 'react';
import './Book_List.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
    } catch (error) {
      setError(error.response ? error.response.data : error.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteBook = async (id) => {
    if (window.confirm("Are you sure you want to delete this book?")) {
      try {
        const response = await axios.delete(`http://localhost:5000/books/${id}`);
        if (response.status === 200) {
          alert("Book Deleted Successfully");
          setBooks((prevBooks) => prevBooks.filter(book => book.bookId !== id)); // Corrected to book.bookId
        } else {
          throw new Error('Failed to delete the book');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  const viewBook = (id) => {
    navigate(`/books/detail?id=${id}`);
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-12">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h4 className="mb-0">Book Details</h4>
              <button className="btn btn-md btn-info rounded" data-toggle="modal" data-target=".bd-example-modal-lg">
                <i className="fa fa-plus" aria-hidden="true"></i> Add Book
              </button>
            </div>
            <div className="card-body">
              {loading && <p className="text-center">Loading...</p>}
              <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                <table className="table table-striped text-center">
                  <thead>
                    <tr>
                      <th>Cust.</th>
                      <th>Book Title</th>
                      <th>Book Author</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {error && (
                      <tr>
                        <td colSpan="4" className="text-danger text-center">{error}</td>
                      </tr>
                    )}
                    {books.length === 0 && !error && (
                      <tr>
                        <td colSpan="4" className="text-center">No books available</td>
                      </tr>
                    )}
                    {books.map((book, index) => (
                      <tr key={book.bookId}>
                        <td>{index + 1}</td>
                        <td>{book.bookTitle}</td>
                        <td>{book.bookAuthor}</td>
                        <td>
                          <button className="btn btn-sm btn-success rounded" onClick={() => viewBook(book.bookId)}>
                            <i className="fa fa-eye" aria-hidden="true"></i>
                          </button>&nbsp;
                          <button className="btn btn-sm btn-primary rounded">
                            <i className="fas fa-edit"></i>
                          </button>&nbsp;
                          <button className="btn btn-sm btn-danger rounded-custom" onClick={() => deleteBook(book.bookId)}>
                            <i className="fa fa-trash" aria-hidden="true"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal for adding books */}
      <div className="modal fade bd-example-modal-lg" tabIndex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            {/* Implement your modal content for adding a book here */}
            <div className="modal-header">
              <h5 className="modal-title">Add New Book</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              {/* Add book form goes here */}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookList;
