import React, { Component } from 'react';
import { fetchBooks } from '../API/Book_Api'; // Import the fetchBooks function
import './Book_List.css';

class BookList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      error: null,
    };
  }

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = async () => {
    try {
      const books = await fetchBooks();
      this.setState({ books });
    } catch (error) {
      this.setState({ error: 'Failed to load books' });
    }
  };

  render() {
    const { books, error } = this.state;

    return (
      <div className="container">
        <div className="row mt-5">
          <div className="col-12">
            <div className="card">
              <div className="card-header">
                <h4>Book Details </h4>
                <div>
                   
                <button className="btn btn-sm btn-info rounded">Add Book</button>
                </div>W
              </div>
              <div className="card-body">
                <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                  <table className="table table-striped text-center">
                    <thead>
                      <tr>
                        <th>Cust.</th>
                        <th>Book Title</th>
                        <th>Book Author</th>
                        <th>Edit</th>
                      </tr>
                    </thead>
                    <tbody>
                      {error && (
                        <tr>
                          <td colSpan="4">{error}</td>
                        </tr>
                      )}
                      {books.map((book, index) => (
                        <tr key={index}>
                          <td>{index + 1}</td>
                          <td>{book.bookTitle}</td>
                          <td>{book.bookAuthor}</td>
                          <td>
                            <button className="btn btn-sm btn-success rounded">View Detail</button>&nbsp;
                            <button className="btn btn-sm btn-primary rounded">Update</button>&nbsp;
                            <button className="btn btn-sm btn-danger rounded-custom">Delete</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default BookList;
