import React, { Component } from 'react';
import './Book_List.css';

class BookList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      error: null,
      loading: true,
    };
  }

  componentDidMount() {
    this.fetchBooks();
  }

  fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:5000/books');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      this.setState({ books: data, loading: false });
    } catch (error) {
      this.setState({ error: error.message, loading: false });
    }
  };

  render() {
    const { books, error, loading } = this.state;

    return (
      <div className="container">
        <div className="row mt-5">
          <div className="col-12">
            <div className="card">
              <div className="card-header d-flex justify-content-between align-items-center">
                <h4 className="mb-0">Book Details</h4>
                <button className="btn btn-md btn-info rounded">Add Book</button>
              </div>
              <div className="card-body">
                {loading && <p className="text-center">Loading...</p>}
                <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                  <table className="table table-striped text-center">
                    <thead>
                      <tr>
                        <th>Cust.</th>
                        <th>Book Title</th>
                        <th>Book Author</th>
                        <th>Edit</th>
                      </tr>
                    </thead>
                    <tbody>
                      {error && (
                        <tr>
                          <td colSpan="4" className="text-danger text-center">{error}</td>
                        </tr>
                      )}
                      {books.length === 0 && !error && (
                        <tr>
                          <td colSpan="4" className="text-center">No books available</td>
                        </tr>
                      )}
                      {books.map((book, index) => (
                        <tr key={book.bookId}>
                          <td>{index + 1}</td>
                          <td>{book.bookTitle}</td>
                          <td>{book.bookAuthor}</td>
                          <td>
                            <button className="btn btn-sm btn-success rounded">View Detail</button>
                            <button className="btn btn-sm btn-primary rounded">Update</button>
                            <button className="btn btn-sm btn-danger rounded-custom" onClick={() => this.deleteBook(book.bookId)}>Delete</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default BookList;
