import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Book_Details = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const id = queryParams.get('id'); // Extract 'id' from query parameters
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookDetail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/books/${id}`);
        setBook(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    if (id) {
      fetchBookDetail();
    }
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container">
    <div className="row mt-5">
      <div className="col-12">
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h4 className="mb-0">Book Details</h4>
            <button className="btn btn-md btn-info rounded">Add Book</button>
          </div>
          <div className="card-body">
            {loading && <p className="text-center">Loading...</p>}
            <div className="table-responsive" id="proTeamScroll" tabIndex="2">
              <table className="table table-striped text-center">
                <thead>
                  <tr>
                    <th>Cust.</th>
                    <th>Book Title</th>
                    <th>Book Author</th>
                    <th>Edit</th>
                  </tr>
                </thead>
                <tbody>
                  {error && (
                    <tr>
                      <td colSpan="4" className="text-danger text-center">{error}</td>
                    </tr>
                  )}
                  {books.length === 0 && !error && (
                    <tr>
                      <td colSpan="4" className="text-center">No books available</td>
                    </tr>
                  )}
                  {books.map((book, index) => (
                    <tr key={book.bookId}>
                      <td>{index + 1}</td>
                      <td>{book.bookTitle}</td>
                      <td>{book.bookAuthor}</td>
                      <td>
                        <button className="btn btn-sm btn-success rounded" onClick={() => viewBook(book.id)}>View Detail</button>&nbsp;
                        <button className="btn btn-sm btn-primary rounded">Update</button>&nbsp;
                        <button className="btn btn-sm btn-danger rounded-custom" onClick={() => deleteBook(book.id)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
    
  );
};

export default Book_Details;

