import React, { useEffect, useState } from 'react';
import './Book_List.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // Use hook here

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const deleteBook = async (id) => {
    if (window.confirm("Are you sure you want to delete this book?")) {
      try {
        const response = await axios.delete(`http://localhost:5000/books/${id}`);
        if (response.status === 200) {
          alert("Book Deleted Successfully");
          setBooks((prevBooks) => prevBooks.filter(book => book.id !== id));
        } else {
          throw new Error('Failed to delete the book');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  const viewBook = (id) => {
    navigate(`/books/detail?id=${id}`); // Use navigate to go to the book details page with a query parameter
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-12">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h4 className="mb-0">Book Details</h4>
              <button className="btn btn-md btn-info rounded "><i class="fa fa-plus" aria-hidden="true"></i> Add Book</button>
            </div>
            <div className="card-body">
              {loading && <p className="text-center">Loading...</p>}
              <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                <table className="table table-striped text-center">
                  <thead>
                    <tr>
                      <th>Cust.</th>
                      <th>Book Title</th>
                      <th>Book Author</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {error && (
                      <tr>
                        <td colSpan="4" className="text-danger text-center">{error}</td>
                      </tr>
                    )}
                    {books.length === 0 && !error && (
                      <tr>
                        <td colSpan="4" className="text-center">No books available</td>
                      </tr>
                    )}
                    {books.map((book, index) => (
                      <tr key={book.bookId}>
                        <td>{index + 1}</td>
                        <td>{book.bookTitle}</td>
                        <td>{book.bookAuthor}</td>
                        <td>
                          <button className="btn btn-sm btn-success rounded" onClick={() => viewBook(book.id)}><i class="fa fa-eye" aria-hidden="true"></i> </button>&nbsp;
                          <button className="btn btn-sm btn-primary rounded"><i class="fas fa-edit"></i> </button>&nbsp;
                          <button className="btn btn-sm btn-danger rounded-custom" onClick={() => deleteBook(book.id)}><i class="fa fa-trash" aria-hidden="true"></i> </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div class="modal fade bd-example-modal-lg" tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
      ...
    </div>
  </div>
</div>
    </div>
  );
};

export default BookList;
