import React, { Component } from 'react';
import { fetchBooks } from './'; // Import the fetchBooks function
import './Book_List.css';

class BookList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      loading: true,
      error: null,
    };
  }

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = async () => {
    try {
      const books = await fetchBooks();
      this.setState({ books, loading: false });
    } catch (error) {
      this.setState({ error: 'Failed to load books', loading: false });
    }
  };

  render() {
    const { books, loading, error } = this.state;

    return (
      <div className="container">
        <div className="row mt-5">
          <div className="col-12">
            <div className="card">
              <div className="card-header">
                <h4>Assign Project List</h4>
              </div>
              <div className="card-body">
                <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                  <table className="table table-striped text-center">
                    <thead>
                      <tr>
                        <th>Cust.</th>
                        <th>Book Title</th>
                        <th>Book Author</th>
                        <th>Edit</th>
                      </tr>
                    </thead>
                    <tbody>
                      {loading && (
                        <tr>
                          <td colSpan="4">Loading...</td>
                        </tr>
                      )}
                      {error && (
                        <tr>
                          <td colSpan="4">{error}</td>
                        </tr>
                      )}
                      {!loading && !error && books.map((book, index) => (
                        <tr key={index}>
                          <td>{index + 1}</td>
                          <td>{book.title}</td>
                          <td>{book.author}</td>
                          <td>
                            <button className="btn btn-sm btn-success rounded">View Details</button>&nbsp;
                            <button className="btn btn-sm btn-primary rounded">Update</button>&nbsp;
                            <button className="btn btn-sm btn-danger rounded-custom">Delete</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default BookList;
