import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddBook = () => {
  const [bookTitle, setBookTitle] = useState('');
  const [bookAuthor, setBookAuthor] = useState('');
  const [publishedDate, setPublishedDate] = useState('');
  const [publisher, setPublisher] = useState('');
  const [overview, setOverview] = useState('');

  const navigate = useNavigate(); // useNavigate hook to handle navigation

  const handleChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'bookTitle':
        setBookTitle(value);
        break;
      case 'bookAuthor':
        setBookAuthor(value);
        break;
      case 'publishedDate':
        setPublishedDate(value);
        break;
      case 'publisher':
        setPublisher(value);
        break;
      case 'overview':
        setOverview(value);
        break;
      default:
        break;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default form submission

    try {
      const response = await axios.post('http://localhost:5000/books', {
        bookTitle,
        bookAuthor,
        publishedDate,
        publisher,
        overview,
      });

      console.log('Book added:', response.data);
      alert('Book added successfully!');

      // Navigate to the book list page
      navigate('/'); // Replace '/books' with your book list route
    } catch (error) {
      console.error('Error adding book:', error);
      alert('There was an error adding the book. Please try again.'); // Show error message
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-md-center">
        <div className="col-12 col-md-11 col-lg-8 col-xl-7 col-xxl-6">
          <div className="bg-white p-4 p-md-5 rounded shadow-sm">
            <form onSubmit={handleSubmit}>
              <div className="row gy-3 gy-md-4 overflow-hidden">
                <div className="col-12">
                  <label htmlFor="bookTitle" className="form-label">
                    Book Title <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="bookTitle"
                    id="bookTitle"
                    value={bookTitle}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-12">
                  <label htmlFor="bookAuthor" className="form-label">
                    Book Author <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="bookAuthor"
                    id="bookAuthor"
                    value={bookAuthor}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-12">
                  <label htmlFor="publishedDate" className="form-label">
                    Published Date <span className="text-danger">*</span>
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    name="publishedDate"
                    id="publishedDate"
                    value={publishedDate}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-12">
                  <label htmlFor="publisher" className="form-label">
                    Published By <span className="text-danger">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="publisher"
                    id="publisher"
                    value={publisher}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-12">
                  <label htmlFor="overview" className="form-label">
                    Overview
                  </label>
                  <textarea
                    className="form-control"
                    name="overview"
                    id="overview"
                    rows="3"
                    value={overview}
                    onChange={handleChange}
                  ></textarea>
                </div>
                <div className="col-12">
                  <div className="d-grid">
                    <button className="btn btn-md btn-primary" type="submit">
                      Add Book
                    </button>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddBook;
