import React, { useEffect, useState } from 'react';
import './Book_List.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [modalVisible, setModalVisible] = useState(false); // State to manage modal visibility
  const navigate = useNavigate(); // Use hook here

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const deleteBook = async (id) => {
    if (window.confirm("Are you sure you want to delete this book?")) {
      try {
        const response = await axios.delete(`http://localhost:5000/books/${id}`);
        if (response.status === 200) {
          alert("Book Deleted Successfully");
          setBooks((prevBooks) => prevBooks.filter(book => book.id !== id));
        } else {
          throw new Error('Failed to delete the book');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  const viewBook = (id) => {
    navigate(`/books/detail?id=${id}`); // Use navigate to go to the book details page with a query parameter
  };

  const handleModalClose = () => {
    setModalVisible(false);
  };

  const handleModalShow = () => {
    setModalVisible(true);
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-12">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h4 className="mb-0">Book Details</h4>
              <button 
                type="button" 
                className="btn btn-info" 
                onClick={handleModalShow} // Show modal on button click
              ><i class="fa fa-plus" aria-hidden="true"></i>

                Add Book
              </button>
            </div>
            <div className="card-body">
              {loading && <p className="text-center">Loading...</p>}
              <div className="table-responsive" id="proTeamScroll" tabIndex="2">
                <table className="table table-striped text-center">
                  <thead>
                    <tr>
                      <th>Cust.</th>
                      <th>Book Title</th>
                      <th>Book Author</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {error && (
                      <tr>
                        <td colSpan="4" className="text-danger text-center">{error}</td>
                      </tr>
                    )}
                    {books.length === 0 && !error && (
                      <tr>
                        <td colSpan="4" className="text-center">No books available</td>
                      </tr>
                    )}
                    {books.map((book, index) => (
                      <tr key={book.bookId}>
                        <td>{index + 1}</td>
                        <td>{book.bookTitle}</td>
                        <td>{book.bookAuthor}</td>
                        <td>
                          <button className="btn btn-sm btn-success rounded" onClick={() => viewBook(book.id)}>
                            <i className="fa fa-eye" aria-hidden="true"></i>
                          </button>&nbsp;
                          <button className="btn btn-sm btn-primary rounded">
                            <i className="fas fa-edit"></i>
                          </button>&nbsp;
                          <button className="btn btn-sm btn-danger rounded-custom" onClick={() => deleteBook(book.id)}>
                            <i className="fa fa-trash" aria-hidden="true"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal for adding books */}
      <div className={`modal fade ${modalVisible ? 'show' : ''}`} style={{ display: modalVisible ? 'block' : 'none' }} tabIndex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden={!modalVisible}>
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Add New Book</h5>
              <button type="button" className="close" onClick={handleModalClose} aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <form>
                <div className="form-group">
                  <label htmlFor="bookTitle">Book Title</label>
                  <input type="text" className="form-control" id="bookTitle" placeholder="Enter book title" />
                </div>
                <div className="form-group">
                  <label htmlFor="bookAuthor">Author</label>
                  <input type="text" className="form-control" id="bookAuthor" placeholder="Enter author name" />
                </div>
                <div className="form-group">
                  <label htmlFor="bookPublishedDate">Published Date</label>
                  <input type="date" className="form-control" id="bookPublishedDate" />
                </div>
                <div className="form-group">
                  <label htmlFor="bookPublisher">Publisher</label>
                  <input type="text" className="form-control" id="bookPublisher" placeholder="Enter publisher name" />
                </div>
                <div className="form-group">
                  <label htmlFor="bookOverview">Overview</label>
                  <textarea className="form-control" id="bookOverview" placeholder="Enter overview"></textarea>
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={handleModalClose}>Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookList;
