import React, { Component } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom

class AddBook extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bookTitle: '',
      bookAuthor: '',
      publishedDate: '',
      publisher: '',
      overview: '',
    };

    // Bind the method to the component
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  async handleSubmit(event) {
    event.preventDefault(); // Prevent the default form submission

    const { bookTitle, bookAuthor, publishedDate, publisher, overview } = this.state;

    try {
      const response = await axios.post('http://localhost:5000/books', {
        bookTitle,
        bookAuthor,
        publishedDate,
        publisher,
        overview,
      });

      // Show alert message
      alert('Book added successfully!');

      // Navigate to the book list page
      this.props.history.push('/books'); // Replace '/books' with your book list route

    } catch (error) {
      console.error('Error adding book:', error);
      alert('There was an error adding the book. Please try again.'); // Show error message
    }
  }

  render() {
    return (
      <div className="container">
        <div className="row justify-content-md-center">
          <div className="col-12 col-md-11 col-lg-8 col-xl-7 col-xxl-6">
            <div className="bg-white p-4 p-md-5 rounded shadow-sm">
              <form onSubmit={this.handleSubmit}>
                <div className="row gy-3 gy-md-4 overflow-hidden">
                  <div className="col-12">
                    <label htmlFor="bookTitle" className="form-label">
                      Book Title <span className="text-danger">*</span>
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      name="bookTitle"
                      id="bookTitle"
                      value={this.state.bookTitle}
                      onChange={this.handleChange}
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label htmlFor="bookAuthor" className="form-label">
                      Book Author <span className="text-danger">*</span>
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      name="bookAuthor"
                      id="bookAuthor"
                      value={this.state.bookAuthor}
                      onChange={this.handleChange}
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label htmlFor="publishedDate" className="form-label">
                      Published Date <span className="text-danger">*</span>
                    </label>
                    <input
                      type="date"
                      className="form-control"
                      name="publishedDate"
                      id="publishedDate"
                      value={this.state.publishedDate}
                      onChange={this.handleChange}
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label htmlFor="publisher" className="form-label">
                      Published By <span className="text-danger">*</span>
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      name="publisher"
                      id="publisher"
                      value={this.state.publisher}
                      onChange={this.handleChange}
                      required
                    />
                  </div>
                  <div className="col-12">
                    <label htmlFor="overview" className="form-label">
                      Overview
                    </label>
                    <textarea
                      className="form-control"
                      name="overview"
                      id="overview"
                      rows="3"
                      value={this.state.overview}
                      onChange={this.handleChange}
                    ></textarea>
                  </div>
                  <div className="col-12">
                    <div className="d-grid">
                      <button className="btn btn-md btn-primary" type="submit">
                        Add Book
                      </button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

// Wrap AddBook with withRouter to use history
import { withRouter } from 'react-router-dom';
export default withRouter(AddBook);
